import React from 'react';
import { Typography, Box, Button, Container, Grid, Card, CardMedia, CardContent, CardActions } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { ShoppingCart as ShoppingCartIcon } from '@mui/icons-material';

const categoryItems = [
  {
    id: 1,
    name: 'Смартфоны',
    image: 'https://via.placeholder.com/300x200?text=Смартфоны',
    description: 'Мощные современные смартфоны от ведущих производителей'
  },
  {
    id: 2,
    name: 'Ноутбуки',
    image: 'https://via.placeholder.com/300x200?text=Ноутбуки',
    description: 'Производительные ноутбуки для работы и развлечений'
  },
  {
    id: 3,
    name: 'Планшеты',
    image: 'https://via.placeholder.com/300x200?text=Планшеты',
    description: 'Стильные и функциональные планшеты для всех задач'
  },
  {
    id: 4,
    name: 'Аксессуары',
    image: 'https://via.placeholder.com/300x200?text=Аксессуары',
    description: 'Качественные аксессуары для вашей электроники'
  }
];

const HomeCategories = () => {
  const navigate = useNavigate();

  const handleCategoryClick = (categoryId) => {
    navigate(`/products?category=${categoryId}`);
  };

  return (
    <Container sx={{ py: 6 }}>
      <Typography variant="h4" component="h2" gutterBottom textAlign="center" fontWeight="bold">
        Категории товаров
      </Typography>
      
      <Typography variant="body1" textAlign="center" color="text.secondary" sx={{ mb: 5 }}>
        Просмотрите нашу подборку товаров в популярных категориях
      </Typography>
      
      <Grid container spacing={4}>
        {categoryItems.map((category) => (
          <Grid item key={category.id} xs={12} sm={6} md={3}>
            <Card 
              sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                transition: 'transform 0.3s, box-shadow 0.3s',
                '&:hover': {
                  transform: 'translateY(-6px)',
                  boxShadow: '0 12px 24px rgba(0,0,0,0.12)'
                }
              }}
            >
              <CardMedia
                component="img"
                height={160}
                image={category.image}
                alt={category.name}
                sx={{ objectFit: 'cover' }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h6" component="div">
                  {category.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {category.description}
                </Typography>
              </CardContent>
              <CardActions>
                <Button 
                  size="medium" 
                  fullWidth 
                  onClick={() => handleCategoryClick(category.id)}
                  variant="outlined"
                >
                  Просмотреть
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default HomeCategories;
